class SelectBox{constructor(){this.getNodes(),this.addEventListeners()}getNodes(){this.title=document.querySelector(".selectbox-title"),this.links=Array.from(document.querySelectorAll(".selectbox-link")),this.resultNodes=Array.from(document.querySelectorAll(".selectbox-result"))}addEventListeners(){this.links.map(e=>e.addEventListener("click",this.changeContent.bind(this)))}changeContent(e){e.preventDefault();const t=e.target.closest("a"),s=t.cloneNode(!0);s.querySelector("span").remove();const l=this.title.lastChild.cloneNode(!0);this.title.innerHTML=s.textContent,this.title.appendChild(l),this.title.click();const o=this.links.indexOf(t);this.showResult(o)}showResult(e){this.resultNodes.map(e=>e.classList.remove("selectbox-result-show")),this.resultNodes[e].classList.add("selectbox-result-show")}}const selectBox=new SelectBox;